import "core-js/modules/es6.function.name";
import "core-js/modules/web.dom.iterable";
import * as THREE from "three";
import TWEEN from "@tweenjs/tween.js";
import { OrbitControls } from "three/examples/jsm/controls/OrbitControls"; //鼠标控制
import SetSolarSystem from "./components/SetSolarSystem";
import PlanetText from "./components/PlanetText";
import planetData from "./planetData.js"; //导入星球数据
import universeImg from "./img/universe.jpg"; //宇宙
import starImg from "./img/star.jpg"; //星辰
import venusAtmosphereImg from "./img/venusAtmosphere.jpg"; //金星大气
import moonImg from "./img/moon.jpg"; //月球
import earthNormalImg from "./img/earthNormal.jpg"; //法线贴图
import earthCloudsImg from "./img/earthClouds.jpg"; //地球云层

var scene = null; //场景(频繁变更的对象放置在vue的data中会导致卡顿)
var camera = null; //相机
var orbitControls = null; //鼠标控件
export default {
  props: {},
  components: {
    SetSolarSystem: SetSolarSystem,
    PlanetText: PlanetText
  },
  data: function data() {
    return {
      dom: "",
      //需要使用canvas的dom
      renderer: "",
      //渲染器
      anId: null,
      //动画id
      planetList: planetData,
      isRevolution: true,
      //公转状态
      isRotation: true,
      //自转状态
      raycaster: new THREE.Raycaster(),
      //光线投射器(用于鼠标点击时获取坐标)
      mouse: new THREE.Vector2(),
      //鼠标点击的二维平面
      clickPlanet: "" //当前点击的星球
    };
  },
  computed: {},
  watch: {},
  created: function created() {},
  mounted: function mounted() {
    this.init();
  },
  methods: {
    //改变设置
    changeSet: function changeSet(data) {
      if (data.key == "isRevolution") {
        this.isRevolution = data.val;
      }
      if (data.key == "isRotation") {
        this.isRotation = data.val;
      }
    },
    //销毁场景
    destroyScene: function destroyScene() {
      cancelAnimationFrame(this.anId); //根据动画id停止动画渲染
      this.renderer.forceContextLoss(); //强制失去上下文
      this.renderer.dispose();
      scene.clear();
      scene = null;
      camera = null;
      orbitControls = null;
      this.dom.innerHTML = null;
      this.renderer = null;
    },
    //初始化场景
    init: function init() {
      var _this = this;
      this.dom = document.getElementById("planetDiv"); //获取dom
      var width = this.dom.clientWidth;
      var height = this.dom.clientHeight;
      scene = new THREE.Scene(); //场景场景
      // scene.add(new THREE.AxesHelper(500));
      camera = new THREE.PerspectiveCamera(45, width / height, 1, 50000); //创建透视相机(视场、长宽比、近面、远面)
      camera.position.set(0, 500, 2700); //设置相机位置
      camera.lookAt(0, 0, 0);
      //创建渲染器
      this.renderer = new THREE.WebGLRenderer({
        antialias: true,
        //抗锯齿
        alpha: true //透明
      });
      this.renderer.setClearColor(0x000000, 0.1); //设置场景透明度
      this.renderer.setSize(width, height); //设置渲染区域尺寸
      this.dom.appendChild(this.renderer.domElement); //将渲染器添加到dom中形成canvas
      this.createUniverse(); //创建宇宙
      this.createStars(); //创建星辰
      this.createLight(); //创建光源
      //遍历行星数据生成星球及其轨道
      this.planetList.forEach(function (e) {
        _this.createSphere(e);
        _this.createTrack(e);
      });
      this.createOrbitControls(); //创建鼠标控制器
      this.render(); //渲染
    },
    //创建宇宙(球形宇宙)
    createUniverse: function createUniverse() {
      var universeGeometry = new THREE.SphereGeometry(7000, 100, 100);
      var universeMaterial = new THREE.MeshLambertMaterial({
        //高光材质
        map: new THREE.TextureLoader().load(universeImg),
        side: THREE.DoubleSide //双面显示
      });
      //宇宙网格
      var universeMesh = new THREE.Mesh(universeGeometry, universeMaterial);
      universeMesh.name = "宇宙";
      scene.add(universeMesh);
    },
    //创建星辰
    createStars: function createStars() {
      var positions = [];
      var colors = [];
      //星辰几何体
      var starsGeometry = new THREE.BufferGeometry();
      //添加星辰的颜色与位置
      for (var i = 0; i < 10000; i++) {
        var vertex = new THREE.Vector3();
        vertex.x = Math.random() * 2 - 1;
        vertex.y = Math.random() * 2 - 1;
        vertex.z = Math.random() * 2 - 1;
        positions.push(vertex.x, vertex.y, vertex.z);
        var color = new THREE.Color();
        color.setRGB(255, 255, 255);
        colors.push(color.r, color.g, color.b);
      }
      starsGeometry.setAttribute("position", new THREE.Float32BufferAttribute(positions, 3));
      starsGeometry.setAttribute("color", new THREE.Float32BufferAttribute(colors, 3));
      //星辰材质
      var starsMaterial = new THREE.PointsMaterial({
        map: new THREE.TextureLoader().load(starImg),
        size: 5,
        blending: THREE.AdditiveBlending,
        fog: true,
        depthTest: false //(不能与blending一起使用)
        // depthWrite: false, //(深度写入)防止星辰在球体前面出现黑块
      });
      //星辰的集合
      var starsMesh = new THREE.Points(starsGeometry, starsMaterial);
      starsMesh.scale.set(7000, 7000, 7000); //设置集合体范围
      scene.add(starsMesh);
    },
    //创建光源
    createLight: function createLight() {
      var ambient = new THREE.AmbientLight(new THREE.Color(0xffffff)); //环境光
      scene.add(ambient);
      var pointLight = new THREE.PointLight(new THREE.Color(0xffffff), 2, 1, 0); //点光源
      pointLight.visible = true;
      pointLight.position.set(0, 0, 0); //点光源在原点充当太阳
      scene.add(pointLight); //点光源添加到场景中
    },
    //创建球体
    createSphere: function createSphere(data) {
      //处理特殊球体
      if (data.name == "太阳") {
        this.createSun(data);
      } else if (data.name == "地球") {
        this.createEarth(data);
      } else if (data.name == "金星") {
        this.createVenus(data);
      } else if (data.name == "土星") {
        this.createSaturn(data);
      } else {
        //其他球体
        var sphereGeometry = new THREE.SphereGeometry(data.size, 100, 100); //球体几何体
        //球体材质
        var sphereMaterial = new THREE.MeshLambertMaterial({
          map: new THREE.TextureLoader().load(data.mapImg)
        });
        var sphereMesh = new THREE.Mesh(sphereGeometry, sphereMaterial); //生成球体网格
        sphereMesh.name = data.name; //网格名字
        sphereMesh.planetMsg = data;
        sphereMesh.isPlanet = true; //标识为星球
        sphereMesh.angle = 0; //添加初始角度
        //球体位置
        sphereMesh.position.set(data.position[0], data.position[1], data.position[2]);
        scene.add(sphereMesh); //球体添加到场景中
      }
    },
    //创建太阳
    createSun: function createSun(data) {
      var sunGroup = new THREE.Group(); //太阳的组
      var sunGeometry = new THREE.SphereGeometry(data.size, 100, 100); //太阳几何体
      var sunMaterial = new THREE.MeshLambertMaterial({
        //太阳材质
        color: new THREE.Color(0xffffff),
        map: new THREE.TextureLoader().load(data.mapImg)
      });
      var sunMesh = new THREE.Mesh(sunGeometry, sunMaterial);
      sunGroup.add(sunMesh);
      //太阳大气几何体
      var sunAtmosphereGeometry = new THREE.SphereGeometry(data.size + 8, 100, 100);
      var sunAtmosphereMaterial = new THREE.MeshLambertMaterial({
        color: new THREE.Color(0xffffff),
        transparent: true,
        opacity: 0.2
      });
      var sunAtmosphereMesh = new THREE.Mesh(sunAtmosphereGeometry, sunAtmosphereMaterial);
      sunGroup.add(sunAtmosphereMesh);
      sunGroup.name = data.name; //网格名字
      sunGroup.planetMsg = data;
      sunGroup.isPlanet = true; //标识为星球
      sunGroup.angle = 0; //添加初始角度
      //球体位置
      sunGroup.position.set(data.position[0], data.position[1], data.position[2]);
      scene.add(sunGroup);
    },
    //创建金星
    createVenus: function createVenus(data) {
      var venusGroup = new THREE.Group(); //金星的组
      var venusGeometry = new THREE.SphereGeometry(data.size, 100, 100); //金星几何体

      var venusMaterial = new THREE.MeshLambertMaterial({
        color: new THREE.Color(0xffffff),
        map: new THREE.TextureLoader().load(data.mapImg)
      });
      var venusMesh = new THREE.Mesh(venusGeometry, venusMaterial);
      venusGroup.add(venusMesh);
      //金星大气几何体
      var venusAtmosphereGeometry = new THREE.SphereGeometry(data.size + 2, 100, 100);
      //金星大气材质
      var venusAtmosphereMaterial = new THREE.MeshLambertMaterial({
        color: new THREE.Color(0xffffff),
        transparent: true,
        opacity: 0.5,
        map: new THREE.TextureLoader().load(venusAtmosphereImg)
      });
      var venusAtmosphereMesh = new THREE.Mesh(venusAtmosphereGeometry, venusAtmosphereMaterial);
      venusGroup.add(venusAtmosphereMesh); //将大气添加到组中
      venusGroup.name = data.name; //网格名字
      venusGroup.planetMsg = data;
      venusGroup.isPlanet = true; //标识为星球
      venusGroup.angle = 0; //添加初始角度
      //球体位置
      venusGroup.position.set(data.position[0], data.position[1], data.position[2]);
      scene.add(venusGroup);
    },
    //创建地球
    createEarth: function createEarth(data) {
      var earthGroup = new THREE.Group(); //地球的组
      var earthGeometry = new THREE.SphereGeometry(data.size, 100, 100); //地球几何体
      //地球材质
      var earthMaterial = new THREE.MeshPhysicalMaterial({
        map: new THREE.TextureLoader().load(data.mapImg),
        normalScale: new THREE.Vector2(10, 10),
        //凹凸深度
        normalMap: new THREE.TextureLoader().load(earthNormalImg) //法线贴图
      });
      var earthMesh = new THREE.Mesh(earthGeometry, earthMaterial); //地球网格
      earthGroup.add(earthMesh); //将地球网格添加到地球组中
      //地球云层几何体
      var earthCloudsGeometry = new THREE.SphereGeometry(data.size + 2, 100, 100);
      //地球云层材质
      var earthCloudsMaterial = new THREE.MeshLambertMaterial({
        color: new THREE.Color(0xffffff),
        transparent: true,
        opacity: 0.4,
        map: new THREE.TextureLoader().load(earthCloudsImg)
      });
      //地球云层网格
      var earthCloudsMesh = new THREE.Mesh(earthCloudsGeometry, earthCloudsMaterial);
      earthGroup.add(earthCloudsMesh); //将地球云层网格添加到地球组中

      //创建月球轨道
      var moonTrackGeometry = new THREE.RingBufferGeometry(
      //圆环几何体
      data.size + 40, data.size + 40.2, 100);
      var moonTrackMaterial = new THREE.MeshLambertMaterial({
        color: 0xffffff,
        side: THREE.DoubleSide
      });
      var moonTrackMesh = new THREE.Mesh(moonTrackGeometry, moonTrackMaterial);
      moonTrackMesh.rotation.set(0.5 * Math.PI, 0, 0);
      earthGroup.add(moonTrackMesh);

      //创建月球
      var moonGeometry = new THREE.SphereGeometry(10, 100, 100);
      var moonMaterial = new THREE.MeshPhysicalMaterial({
        map: new THREE.TextureLoader().load(moonImg),
        normalScale: new THREE.Vector2(10, 10) //凹凸深度
      });
      var moonMesh = new THREE.Mesh(moonGeometry, moonMaterial);
      moonMesh.position.set(data.size + 40, 0, 0);
      earthGroup.add(moonMesh);
      earthGroup.name = data.name; //网格名字
      earthGroup.planetMsg = data;
      earthGroup.isPlanet = true; //标识为星球
      earthGroup.angle = 0; //添加初始角度
      //球体位置
      earthGroup.position.set(data.position[0], data.position[1], data.position[2]);
      scene.add(earthGroup);
    },
    //创建土星
    createSaturn: function createSaturn(data) {
      var saturnGroup = new THREE.Group(); //土星的组
      var saturnGeometry = new THREE.SphereGeometry(data.size, 100, 100); //土星几何体
      var saturnMaterial = new THREE.MeshLambertMaterial({
        map: new THREE.TextureLoader().load(data.mapImg) //土星材质
      });
      var saturnMesh = new THREE.Mesh(saturnGeometry, saturnMaterial); //土星网格
      saturnGroup.add(saturnMesh); //将土星网格添加到地球组中
      //创建土星环1
      var saturnTrackGeometry1 = new THREE.RingBufferGeometry(
      //圆环几何体
      data.size + 10, data.size + 25, 100);
      var saturnTrackMaterial1 = new THREE.MeshLambertMaterial({
        transparent: true,
        opacity: 0.8,
        color: 0xc0ad87,
        side: THREE.DoubleSide
      });
      var saturnTrackMesh1 = new THREE.Mesh(saturnTrackGeometry1, saturnTrackMaterial1);
      saturnTrackMesh1.rotation.set(0.5 * Math.PI, 0, 0);
      //创建土星环2
      var saturnTrackGeometry2 = new THREE.RingBufferGeometry(
      //圆环几何体
      data.size + 26, data.size + 30, 100);
      var saturnTrackMaterial2 = new THREE.MeshLambertMaterial({
        transparent: true,
        opacity: 0.5,
        color: 0xc0ad87,
        side: THREE.DoubleSide
      });
      var saturnTrackMesh2 = new THREE.Mesh(saturnTrackGeometry2, saturnTrackMaterial2);
      saturnTrackMesh2.rotation.set(0.5 * Math.PI, 0, 0);
      //创建土星环3
      var saturnTrackGeometry3 = new THREE.RingBufferGeometry(
      //圆环几何体
      data.size + 30.1, data.size + 32, 100);
      var saturnTrackMaterial3 = new THREE.MeshLambertMaterial({
        transparent: true,
        opacity: 0.3,
        color: 0xc0ad87,
        side: THREE.DoubleSide
      });
      var saturnTrackMesh3 = new THREE.Mesh(saturnTrackGeometry3, saturnTrackMaterial3);
      saturnTrackMesh3.rotation.set(0.5 * Math.PI, 0, 0);
      saturnGroup.add(saturnTrackMesh1); //将网格添加到组中
      saturnGroup.add(saturnTrackMesh2);
      saturnGroup.add(saturnTrackMesh3);
      saturnGroup.name = data.name; //网格名字
      saturnGroup.planetMsg = data;
      saturnGroup.isPlanet = true; //标识为星球
      saturnGroup.angle = 0; //添加初始角度
      //球体位置
      saturnGroup.position.set(data.position[0], data.position[1], data.position[2]);
      scene.add(saturnGroup);
    },
    //创建球体轨迹
    createTrack: function createTrack(data) {
      if (data.name == "太阳") {
        //去除太阳中心由圆环形成的圆形
        return;
      }
      //创建轨迹
      var trackGeometry = new THREE.RingBufferGeometry(
      //圆环几何体
      data.position[0], data.position[0] + 2, 1000);
      //圆环材质
      var trackMaterial = new THREE.LineBasicMaterial({
        color: 0xffffff,
        side: THREE.DoubleSide
      });
      var trackMesh = new THREE.Mesh(trackGeometry, trackMaterial);
      trackMesh.position.set(0, 0, 0); //轨道位置
      trackMesh.rotation.set(0.5 * Math.PI, 0, 0); //旋转轨道至水平
      scene.add(trackMesh);
    },
    //创建鼠标控件
    createOrbitControls: function createOrbitControls() {
      orbitControls = new OrbitControls(camera, this.renderer.domElement);
      orbitControls.enablePan = false; //右键平移拖拽
      orbitControls.enableZoom = true; //鼠标缩放
      orbitControls.enableDamping = true; //滑动阻尼
      orbitControls.dampingFactor = 0.05; //(默认.25)
      orbitControls.minDistance = 100; //相机距离目标最小距离
      orbitControls.maxDistance = 2700; //相机距离目标最大距离
      // orbitControls.maxPolarAngle = (Math.PI / 4) * 3; //y旋转角度范围
      // orbitControls.minPolarAngle = Math.PI / 4;
      orbitControls.autoRotate = true; //自转(相机)
      orbitControls.autoRotateSpeed = 0; //自转速度
    },
    //渲染
    render: function render() {
      //请求动画帧，屏幕每刷新一次调用一次，绑定屏幕刷新频率
      this.anId = requestAnimationFrame(this.render); //记录下动画id可用于销毁场景
      orbitControls.update(); //鼠标控件实时更新
      this.renderer.render(scene, camera);
      //控制公转
      if (this.isRevolution) {
        this.sphereRevolution(this.planetList); //球体公转
      }
      if (this.isRotation) {
        this.sphereRotation(this.planetList); //球体自转
      }
      //监听画布双击事件
      document.getElementById("planetDiv") && document.getElementById("planetDiv").addEventListener("dblclick", this.handleDblclick, false);
      TWEEN.update(); //更新动画
    },
    //双击事件
    handleDblclick: function handleDblclick(e) {
      var _this2 = this;
      var dom = document.getElementById("planetDiv");
      var width = dom.clientWidth; //窗口宽度
      var height = dom.clientHeight; //窗口高度
      //将鼠标点击位置的屏幕坐标转换成threejs中的标准坐标
      this.mouse.x = e.offsetX / width * 2 - 1;
      this.mouse.y = -(e.offsetY / height) * 2 + 1;
      // 通过鼠标点的位置和当前相机的矩阵计算出raycaster
      this.raycaster.setFromCamera(this.mouse, camera);
      //生成星球网格列表
      var palnetMeshList = [];
      scene.children.forEach(function (p) {
        if (p.name !== "") {
          palnetMeshList.push(p);
        }
      });
      // 获取raycaster直线和星球网格列表相交的集合
      var intersects = this.raycaster.intersectObjects(palnetMeshList);
      //判断是否点击到虚无的太空
      if (intersects.length == 0) {
        return;
      }
      //判断是否是行星
      if (intersects[0].object.isPlanet) {
        this.clickPlanet = intersects[0].object;
      } else {
        this.clickPlanet = intersects[0].object.parent;
      }
      // console.log(this.clickPlanet);
      //获取球体半径
      var planetR = "";
      this.planetList.forEach(function (e) {
        if (e.name == _this2.clickPlanet.name) {
          planetR = e.size;
        }
      });
      //相机新位置
      var newP = {
        x: this.clickPlanet.position.x,
        y: this.clickPlanet.position.y + planetR,
        z: this.clickPlanet.position.z + 2.5 * planetR
      };
      //双击到星球需要停止公转（双击虚空需反转公转状态）
      if (this.clickPlanet.type !== "Scene") {
        this.isRevolution = false;
        this.isRotation = false;

        //点击后传入参数飞向星球
        this.flyTo(camera.position, orbitControls.target, newP, this.clickPlanet.position, 2000);
      } else {
        this.isRevolution = !this.isRevolution;
        this.isRotation = !this.isRotation;
      }
    },
    //飞向对象(旧相机位置，旧对象位置，新相机位置，新对象位置，动画时间，回调)
    flyTo: function flyTo(oldP, oldT, newP, newT, time, callBack) {
      if (TWEEN) {
        var tween = new TWEEN.Tween({
          x1: oldP.x,
          // 相机x
          y1: oldP.y,
          // 相机y
          z1: oldP.z,
          // 相机z
          x2: oldT.x,
          // 控制点的中心点x
          y2: oldT.y,
          // 控制点的中心点y
          z2: oldT.z // 控制点的中心点z
        });
        tween.to({
          x1: newP.x,
          y1: newP.y,
          z1: newP.z,
          x2: newT.x,
          y2: newT.y,
          z2: newT.z
        }, time);
        tween.onUpdate(function (object) {
          camera.position.set(object.x1, object.y1, object.z1);
          orbitControls.target.x = object.x2;
          orbitControls.target.y = object.y2;
          orbitControls.target.z = object.z2;
          orbitControls.update();
        });
        tween.onComplete(function () {
          callBack && callBack();
        });
        tween.easing(TWEEN.Easing.Cubic.InOut);
        tween.start();
      }
    },
    //球体自转
    sphereRotation: function sphereRotation(data) {
      scene.children.forEach(function (e) {
        //过滤出星球
        if (e.isPlanet) {
          var _planetData = data.filter(function (d) {
            return d.name == e.name;
          })[0];
          if (e.name == "土星") {
            e.rotation.x = 0.05 * 2 * Math.PI;
            // return;
          }
          //天王星自转轴特殊
          if (e.name == "天王星") {
            e.rotation.z = e.rotation.z + _planetData.rotation >= 2 * Math.PI ? 0 : e.rotation.z + _planetData.rotation;
            return;
          }
          e.rotation.y = e.rotation.y + _planetData.rotation >= 2 * Math.PI ? 0 : e.rotation.y + _planetData.rotation;
        }
      });
    },
    //球体公转
    sphereRevolution: function sphereRevolution(data) {
      scene.children.forEach(function (e) {
        //过滤出星球
        if (e.isPlanet) {
          var _planetData2 = data.filter(function (d) {
            return d.name == e.name;
          })[0]; //获取球体数据
          e.angle = e.angle + _planetData2.revolution >= 2 * Math.PI ? 0 : e.angle + _planetData2.revolution;
          e.position.set(_planetData2.position[0] * Math.sin(e.angle), 0, _planetData2.position[0] * Math.cos(e.angle));
        }
      });
    }
  }
};
